-- Données du CAC 40 (2015-2024)
-- chiffre_affaires, benefice, dette, capitalisation, capitaux_propres : en millions d'euros
-- dividende : rendement en pourcentage
-- employes : nombre d'employés

DROP TABLE IF EXISTS finances;
DROP TABLE IF EXISTS entreprises;

CREATE TABLE entreprises (
    id INTEGER PRIMARY KEY,
    nom VARCHAR(255) NOT NULL,
    secteur VARCHAR(255) NOT NULL,
    employes INTEGER NOT NULL
);

CREATE TABLE finances (
    id INTEGER PRIMARY KEY,
    entreprise_id INTEGER NOT NULL,
    annee INTEGER NOT NULL,
    chiffre_affaires INTEGER NOT NULL,
    benefice INTEGER NOT NULL,
    dette INTEGER NOT NULL,
    dividende FLOAT NOT NULL,
    capitalisation INTEGER NOT NULL,
    capitaux_propres INTEGER NOT NULL,
    FOREIGN KEY (entreprise_id) REFERENCES entreprises(id)
);

